import{_ as p,o as t,c as e,d as a}from"./app.b5d7ca12.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"daily/\u611F\u609F.md","lastUpdated":1670334937000}'),s={name:"daily/\u611F\u609F.md"},_=a('<details class="details custom-block"><summary>\u5973\u751F\u60F3\u8981\u4E00\u4E2A\u4F53\u9762\u7684\u5A5A\u793C\u6709\u9519\u5417\uFF1F</summary><p>\u4F60\u7CBE\u5FC3\u7B79\u5907\u4E86\u4E00\u4E2A\u5A5A\u793C\uFF0C\u6BCF\u4E2A\u7EC6\u8282\u90FD\u6781\u81F4\u63A8\u6572\u529B\u6C42\u5B8C\u7F8E\u3002\u597D\u51E0\u4E07\u7684\u5A5A\u7EB1\u54AC\u7259\u4E70\uFF0C\u56E0\u4E3A\u4F60\u89C9\u5F97\u592A\u7F8E\u4E86\uFF1B\u597D\u51E0\u4E07\u7684\u821E\u53F0\u6548\u679C\u54AC\u7259\u4E0A\uFF0C\u56E0\u4E3A\u4F60\u89C9\u5F97\u592A\u9707\u64BC\u4E86\uFF1B\u8FDC\u8FD1\u95FB\u540D\u7684\u53F8\u4EEA\u3001\u91D1\u96D5\u7389\u7422\u7684\u4F34\u5A18\u56E2\u3001\u7EDA\u4E3D\u7684\u706F\u5149\u3001\u7CBE\u5FC3\u51C6\u5907\u7684\u5C0F\u8282\u76EE\u548C\u4F34\u624B\u793C\u2026\u2026\u4F60\u7684\u5A5A\u793C\u5BF9\u4F60\u6765\u8BF4\u582A\u79F0\u5B8C\u7F8E\u3002</p><p>\u6211\u5165\u5E2D\uFF0C\u5148\u662F\u793C\u8C8C\u6027\u5730\u55D1\u70B9\u74DC\u5B50\uFF0C\u540E\u6765\u53E3\u6E34\uFF0C\u5012\u4E86\u676F\u6930\u6C41\uFF0C\u518D\u540E\u6765\u5C1D\u4E86\u5C1D\u51B7\u76D8\uFF0C\u54CE\u54DF\u8FD8\u4E0D\u9519\uFF0C\u591A\u5403\u51E0\u5757\u2026\u2026\u76F4\u5230\u8F6C\u573A\u97F3\u4E50\u54CD\u8D77\uFF0C\u53F8\u4EEA\u5F00\u59CB\u5FF5\u53E8\u8D77\u5343\u7BC7\u4E00\u5F8B\u7684\u795D\u8BCD\uFF0C\u7EC8\u4E8E\u4E0A\u70ED\u83DC\u4E86\u3002\u5927\u5BB6\u5F88\u6587\u660E\uFF0C\u4F46\u4E00\u76D8\u94C1\u6253\u7684\u5341\u4EBA\u4EFD\uFF0C\u5BA2\u6C14\u5C31\u53EF\u80FD\u5403\u4E0D\u7740\u4E86\u3002\u4E8E\u662F\u684C\u76D8\u5F00\u59CB\u8F6C\u52A8\u3002</p><p>\u7EC8\u4E8E\uFF0C\u65B0\u5A18\u5165\u573A\u3002\u5927\u5385\u4E3B\u706F\u88AB\u7184\u706D\uFF0C\u4E00\u65F6\u95F4\u6211\u4E0D\u592A\u9002\u5E94\uFF0C\u684C\u9762\u7684\u83DC\u770B\u4E0D\u592A\u6E05\u695A\u4E86\u3002\u6211\u4E00\u8FB9\u9F13\u638C\u4E00\u8FB9\u9002\u5E94\u7740\u5149\u7EBF\uFF0C\u51E0\u79D2\u949F\u540E\uFF0C\u6211\u53C8\u80FD\u770B\u6E05\u4E86\u3002\u6CA1\u60F3\u5230\uFF0C\u5927\u5BB6\u6062\u590D\u7684\u90FD\u5F88\u5FEB\uFF0C\u660F\u6697\u7684\u706F\u5149\u5E76\u4E0D\u5F71\u54CD\u5927\u5BB6\u8FDB\u98DF\u3002</p><p>\u6211\u62AC\u5934\u5F80\u95E8\u53E3\u671B\u4E86\u4E00\u773C\uFF0C\u5C31\u4E00\u773C\uFF0C\u5927\u6982\u534A\u79D2\u949F\uFF0C\u4E45\u4E00\u70B9\u90FD\u662F\u5BF9\u98DF\u7269\u7684\u4E0D\u5C0A\u91CD\u3002\u6B64\u65F6\u7684\u4F60\u7AD9\u5728\u95E8\u5916\u7B49\u7740\u5F00\u95E8\uFF0C\u4F60\u53EF\u80FD\u5F88\u6FC0\u52A8\uFF0C\u751A\u81F3\u8981\u6D41\u6CEA\uFF0C\u8FD9\u5F88\u6B63\u5E38\uFF0C\u7ECF\u5E38\u6709\u5927\u95E8\u5F00\u542F\u8FDB\u6765\u4E2A\u54ED\u7684\u7A00\u91CC\u54D7\u5566\u7684\u65B0\u5A18\uFF0C\u628A\u5316\u5986\u5E08\u6298\u78E8\u7684\u4E0D\u6210\u4EBA\u6837\u3002</p><p>\u63A5\u4E0B\u6765\u957F\u8BDD\u77ED\u8BF4\uFF0C\u53F8\u4EEA\u4E00\u901A\u5F69\u8679\u87BA\u65CB\u5C41\u540E\uFF0C\u5927\u95E8\u987A\u5229\u6253\u5F00\uFF0C\u6CA1\u6709\u5361\u4F4F\uFF0C\u95E8\u628A\u624B\u4E5F\u8FD8\u5065\u5728\uFF0C\u8FD9\u65F6\u5927\u5385\u7684\u97F3\u54CD\u4F20\u6765\u4E49\u52C7\u519B\u8FDB\u884C\uFF0C\u554A\u4E0D\u662F\uFF0C\u5A5A\u793C\u8FDB\u884C\u66F2\u3002\u4F60\u8FC8\u7740\u575A\u5B9A\u7684\u6B65\u4F10\u5C0F\u5FC3\u63A7\u5236\u7740\u978B\u8DDF\uFF0C\u56E0\u4E3A\u8DDF\u5BF9\u4F60\u6765\u8BF4\u9AD8\u4E86\u70B9\uFF0C\u8FD9\u978B\u4F60\u7A7F\u7740\u4E0D\u592A\u4E60\u60EF\uFF0C\u56E0\u4E3A\u978B\u662F\u4ECE\u5F71\u697C\u79DF\u7684\uFF0C\u4F60\u6CA1\u60F3\u5230\u81EA\u5DF1\u4E70\u7684\u7CBE\u7F8E\u9AD8\u8DDF\u978B\u5C45\u7136\u4E0D\u642D\u5A5A\u7EB1\uFF0C\u88AB\u8FEB\u8E29\u7740\u4E00\u53CC\u4E0D\u77E5\u9053\u591A\u5C11\u4EBA\u7A7F\u8FC7\u7684\u9AD8\u8DDF\u978B\u3002</p><p>\u6709\u60CA\u65E0\u9669\u5730\uFF0C\u4F60\u6765\u5230T\u53F0\u672B\u7AEF\u3002\u4F60\u770B\u5230\u6709\u4EBA\u62FF\u51FA\u624B\u673A\u7ED9\u4F60\u62CD\u7167\uFF0C\u6B64\u523B\uFF0C\u4F60\u5C31\u662F\u5927\u5385\u91CC\u6700\u9753\u7684\u4ED4\u3002\u62B1\u6B49\uFF0C\u6211\u6CA1\u6709\u7ED9\u4F60\u62CD\u7167\uFF0C\u56E0\u4E3A\u624B\u673A\u4F4E\u7AEF\uFF0C\u5927\u5385\u592A\u6697\uFF0C\u8DDF\u4F60\u4E5F\u4E0D\u719F\u3002\u6240\u4EE5\uFF0C\u6211\u7EE7\u7EED\u5403\u7740\u624B\u91CC\u7684\u534A\u53EA\u8783\u87F9\uFF0C\u5076\u5C14\u62AC\u773C\u770B\u4E00\u770B\u6709\u6CA1\u4EC0\u4E48\u7A81\u53D1\u60C5\u51B5\u53D1\u751F\u3002\u5982\u679C\u6709\uFF0C\u90A3\u4F5C\u4E3A\u4E00\u7EBF\u89C2\u5BDF\u5458\uFF0C\u6211\u6709\u4E49\u52A1\u628A\u4E8B\u4EF6\u59CB\u672B\u5F55\u4E0B\u6765\u53D1\u5230\u201C\u76F8\u4EB2\u76F8\u7231\u4E00\u5BB6\u4EBA\u201D\u3002\u5F53\u7136\uFF0C\u591A\u6570\u65F6\u5019\u90FD\u662F\u6309\u90E8\u5C31\u73ED\uFF0C\u6CA1\u6709\u7279\u6B8A\u60C5\u51B5\u3002</p><p>\u4F60\u9009\u7684\u9152\u5E97\u4E0D\u9519\uFF0C\u4E0A\u83DC\u7684\u8282\u594F\u628A\u63E1\u7684\u5F88\u597D\uFF0C\u5728\u8FD9\u4E2A\u5A5A\u793C\u7684\u9AD8\u6F6E\u9636\u6BB5\uFF0C\u4E0A\u83DC\u660E\u663E\u53D8\u6162\u8BB8\u591A\uFF0C\u4EE5\u6B64\u8BA9\u5927\u5BB6\u591A\u5F80\u53F0\u4E0A\u770B\u770B\uFF0C\u522B\u7279\u4E48\u5149\u987E\u7740\u5403\u3002\u4F46\u5F88\u660E\u663E\uFF0C\u5927\u5BB6\u8FD8\u662F\u5149\u987E\u7740\u5403\uFF0C\u6240\u4EE5\uFF0C\u684C\u9762\u7684\u83DC\u54C1\u5F88\u5FEB\u5C31\u88AB\u6E05\u7A7A\u4E86\u3002\u670D\u52A1\u5458\u770B\u4E86\u770B\u8868\uFF0C\u53C8\u770B\u4E86\u770B\u5927\u5385\u7684\u684C\u9762\uFF0C\u679C\u65AD\u62FF\u51FA\u5BF9\u8BB2\u673A\uFF0C\u201C\u5BCC\u8D35\u5385\uFF0C\u5BCC\u8D35\u5385\u7EE7\u7EED\u4E0A\u83DC\uFF0C\u8FD9\u7FA4\u903C\u90FD\u5403\u5149\u4E86\uFF01\u201D\u3002</p><p>\u5728\u8FD9\u573A\u5BBE\u5BA2\u4E0E\u9152\u5E97\u65B9\u7684\u535A\u5F08\u4E2D\uFF0C\u56E2\u7ED3\u7684\u5BBE\u5BA2\u5360\u4E86\u4E0A\u98CE\uFF0C\u9152\u5E97\u5F00\u59CB\u6062\u590D\u4E0A\u83DC\u901F\u5EA6\u3002\u5927\u5BB6\u6109\u5FEB\u5730\u8BA8\u8BBA\u7740\u83DC\u54C1\uFF0C\u8FD9\u4E2A\u597D\u5403\u3001\u90A3\u4E2A\u8D35\u2026\u2026\u5E2D\u95F4\u6709\u4EBA\u95EE\u6211\uFF0C\u65B0\u5A18\u53EB\u5565\u540D\u554A\uFF1F</p><p>\u6211\u54EA\u77E5\u9053\uFF0C\u6211\u8DDF\u4EB2\u621A\u6765\u7684\uFF0C\u65B0\u90CE\u662F\u8C01\u6211\u4E5F\u4E0D\u77E5\u9053\u554A\uFF0C\u53CD\u6B63\u662F\u4E2A\u4EB2\u621A\u3002</p><p>\u5A5A\u793C\u7684\u8FDB\u7A0B\u8282\u594F\u628A\u63E1\u7684\u5F88\u597D\uFF0C\u53F8\u4EEA\u5728\u53F0\u4E0A\u53C8\u5531\u53C8\u8DF3\u62D6\u4E86\u8DB3\u591F\u957F\u7684\u65F6\u95F4\uFF0C\u4EE5\u81F3\u4E8E\u5927\u5BB6\u90FD\u5403\u9971\u4E86\uFF0C\u5750\u5728\u684C\u8FB9\u65E0\u6240\u4E8B\u4E8B\u3002\u4E8E\u662F\u5F88\u591A\u4EBA\u5F00\u59CB\u5237\u624B\u673A\uFF0C\u4E00\u4E9B\u4EBA\u5F00\u59CB\u62BD\u70DF\uFF0C\u4E09\u4E09\u4E24\u4E24\u7684\u5F00\u59CB\u804A\u5929\u656C\u9152\u3002\u5927\u5385\u65F6\u4E0D\u65F6\u4F20\u6765\u723D\u6717\u7684\u7B11\u58F0\uFF0C\u201C\u54C8\u54C8\u54C8\uFF0C\u4E0D\u80FD\u559D\u4E0D\u80FD\u559D\uFF0C\u4E00\u4F1A\u8FD8\u5F97\u5F00\u8F66\u5462\u3002\u201D</p><p>\u7EC8\u4E8E\uFF0C\u5927\u5BB6\u7B49\u5230\u4E86\u65B0\u4EBA\u656C\u9152\u73AF\u8282\uFF0C\u684C\u9762\u5C01\u7EA2\u5305\u7684\u4EBA\u4EEC\u5F00\u59CB\u7FFB\u5305\u6478\u515C\uFF0C\u6709\u7684\u4EBA\u4E8B\u5148\u51C6\u5907\u597D\u4E86\uFF0C\u653E\u5728\u7EA2\u5305\u91CC\uFF0C\u6709\u7684\u4EBA\u638F\u51FA\u4E00\u53E0\uFF0C\u663E\u6446\u4E00\u4E0B\uFF0C\u70B9\u51FA\u51E0\u5F20\uFF0C\u518D\u628A\u90A3\u53E0\u653E\u56DE\u515C\u91CC\u3002</p><p>\u65B0\u4EBA\u656C\u4E00\u684C\uFF0C\u90A3\u684C\u5C31\u8D70\u6389\u51E0\u4E2A\uFF0C\u518D\u656C\u4E00\u684C\uFF0C\u53C8\u8D70\u6389\u51E0\u4E2A\u3002\u5168\u90E8\u656C\u5B8C\uFF0C\u4EBA\u5269\u4E00\u534A\u591A\u3002\u6B64\u65F6\uFF0C\u8FD8\u6709\u83DC\u6CA1\u4E0A\uFF0C\u5C4F\u5E55\u4E0A\u4F60\u7CBE\u5FC3\u51C6\u5907\u7684VCR\u8FD8\u6CA1\u653E\u5B8C\uFF0C\u4F46\u4ECE\u4F60\u656C\u5B8C\u9152\u5F00\u59CB\uFF0C\u5A5A\u793C\u5DF2\u7ECF\u9ED8\u8BA4\u7ED3\u675F\u4E86\u3002\u670D\u52A1\u5458\u50AC\u4FC3\u7740\u540E\u53A8\u5C3D\u5FEB\u628A\u83DC\u4E0A\u9F50\uFF0C\u4F60\u62C9\u7740\u7231\u4EBA\u8D76\u7D27\u8DD1\u5230\u95E8\u53E3\u9001\u5BA2\u3002\u957F\u65F6\u95F4\u7684\u5047\u7B11\u8BA9\u4F60\u9762\u5BB9\u50F5\u786C\uFF0C\u751A\u81F3\u5FEB\u8981\u62BD\u7B4B\uFF0C\u656C\u9152\u4E0D\u65AD\u5730\u97A0\u8EAC\u8BA9\u4F60\u6709\u70B9\u8111\u6DE4\u8840\uFF0C\u4F46\u4F60\u5F97\u575A\u6301\u4F4F\uFF0C\u7B11\u7740\u9001\u8D70\u5BBE\u5BA2\u3002\u540E\u6765\u4F60\u56DE\u5934\u671B\u5411\u5927\u5385\uFF0C\u5269\u4E0B\u96F6\u96F6\u6563\u6563\u51E0\u4E2A\u4EBA\uFF0C\u57FA\u672C\u90FD\u662F\u719F\u6089\u7684\u4EB2\u621A\u3002\u684C\u4E0A\u4F60\u7CBE\u5FC3\u51C6\u5907\u7684\u4F34\u624B\u793C\uFF0C\u6709\u4E9B\u4EBA\u6CA1\u62FF\uFF0C\u6709\u4E9B\u4EBA\u5F53\u573A\u62C6\u4E86\uFF0C\u6216\u8BB8\u5403\u4E86\u4E2A\u7CD6\uFF0C\u628A\u5269\u4E0B\u7684\u9057\u5F03\u5728\u684C\u9762\u3002\u4F60\u53F9\u4E86\u53E3\u6C14\uFF0C\u6709\u4E9B\u60CB\u60DC\u3002</p><p>\u5A5A\u793C\u8FC7\u540E\u4E00\u4E2A\u591A\u6708\uFF0C\u5728\u4F60\u7684\u50AC\u4FC3\u4E0B\uFF0C\u5F71\u697C\u7EC8\u4E8E\u5C06\u4F60\u7684\u5A5A\u793C\u5F55\u50CF\u6574\u7406\u597D\u7ED9\u4F60\u3002\u4F60\u5728\u7535\u8111\u4E0A\u6253\u5F00\u90A3\u4E2A4G\u591A\u7684\u89C6\u9891\u6587\u4EF6\uFF0C\u51C6\u5907\u56DE\u5473\u4E00\u4E0B\u90A3\u4E2A\u7F8E\u597D\u7684\u65F6\u523B\u3002\u770B\u4E86\u4E0D\u5230\u4E24\u5206\u949F\uFF0C\u5C31\u542C\u5230\u4F60\u5A46\u5A46\u5728\u558A\u4F60\uFF0C\u201C\u59B9\u513F\u554A\uFF01\u5FEB\u4E0B\u96E8\u4E86\uFF01\u5FEB\u53BB\u628A\u8863\u670D\u6536\u4E86\uFF01\u201D</p><p>\u4F60\u6309\u4E0B\u6682\u505C\u952E\uFF0C\u98DE\u5954\u5230\u9633\u53F0\u6536\u8863\u670D\u3002\u6536\u5B8C\u8863\u670D\u5F97\u53E0\u5427\uFF0C\u53E0\u5B8C\u5F97\u653E\u8863\u67DC\u5427\u3002\u8FD9\u4E8B\u5E72\u5B8C\uFF0C\u4F60\u6CA1\u4E86\u5174\u81F4\uFF0C\u638F\u51FA\u624B\u673A\u5237\u8D77\u6296\u97F3\u3002\u4E0E\u6296\u97F3\u91CC\u6781\u5C3D\u5962\u4F88\u6216\u82B1\u6837\u767E\u51FA\u7684\u5A5A\u793C\u76F8\u6BD4\uFF0C\u4F60\u7684\u5A5A\u793C\u597D\u50CF\u4E5F\u5C31\u5E73\u5E73\u65E0\u5947\u3002\u4E8E\u662F\uFF0C\u82E5\u5E72\u5E74\u540E\uFF0C\u4F60\u8FD8\u662F\u6CA1\u6709\u5B8C\u6574\u5730\u628A\u8FD9\u4E2A\u89C6\u9891\u770B\u4E00\u904D\u3002\u53C8\u8FC7\u4E86\u82E5\u5E72\u5E74\uFF0C\u7535\u8111\u574F\u4E86\uFF0C\u5BB6\u91CC\u4E70\u4E86\u65B0\u7535\u8111\uFF0C\u89C6\u9891\u4E5F\u5F04\u4E22\u4E86\u3002\u4F60\u60F3\uFF0C\u4E22\u4E86\u5C31\u4E22\u4E86\u5427\uFF0C\u6CA1\u4EBA\u770B\u3002</p></details>',1),o=[_];function c(r,d,n,i,l,m){return t(),e("div",null,o)}const T=p(s,[["render",c]]);export{f as __pageData,T as default};
